mapper.insert:
select nextval('SEQ_APP_DEF')
insert into T_CACHE_DEF (
) values ( #{id, jdbcType = VARCHAR}, #{cacheName, jdbcType = VARCHAR}, #{appName, jdbcType = VARCHAR}, #{cacheZkPath, jdbcType = VARCHAR}, #{cacheRedisKey, jdbcType = VARCHAR}, #{cacheInsClass, jdbcType = VARCHAR}, #{bridgeClass, jdbcType = VARCHAR}, #{feildKeys, jdbcType = VARCHAR}, #{isGlobal, jdbcType = VARCHAR}, #{isDurable, jdbcType = VARCHAR}, #{durationOfMinutes, jdbcType = INTEGER}, current_timestamp, #{status, jdbcType = VARCHAR}, #{updateUserId, jdbcType = VARCHAR}, #{createUserId, jdbcType = VARCHAR}, current_timestamp, current_timestamp )

mapper.select:
select

from T_CACHE_DEF


        and ID = #{id,jdbcType=VARCHAR}


        and CACHE_NAME = #{cacheName,jdbcType=VARCHAR}


        and APP_NAME = #{appName,jdbcType=VARCHAR}


        and CACHE_ZK_PATH = #{cacheZkPath,jdbcType=VARCHAR}


        and CACHE_REDIS_KEY = #{cacheRedisKey,jdbcType=VARCHAR}


        and CACHE_INS_CLASS = #{cacheInsClass,jdbcType=VARCHAR}


        and BRIDGE_CLASS = #{bridgeClass,jdbcType=VARCHAR}


        and FEILD_KEYS = #{feildKeys,jdbcType=VARCHAR}


        and IS_GLOBAL = #{isGlobal,jdbcType=VARCHAR}


        and IS_DURABLE = #{isDurable,jdbcType=VARCHAR}


        and DURATION_OF_MINUTES = #{durationOfMinutes,jdbcType=INTEGER}


        and LAST_LOAD_DATETIME = #{lastLoadDatetime,jdbcType=TIMESTAMP}


        and STATUS = #{status,jdbcType=VARCHAR}


        and UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}

mapper.update:
update T_CACHE_DEF
ID = #{id,jdbcType=VARCHAR}, CACHE_NAME = #{cacheName,jdbcType=VARCHAR}, APP_NAME = #{appName,jdbcType=VARCHAR}, CACHE_ZK_PATH = #{cacheZkPath,jdbcType=VARCHAR}, CACHE_REDIS_KEY = #{cacheRedisKey,jdbcType=VARCHAR}, CACHE_INS_CLASS = #{cacheInsClass,jdbcType=VARCHAR}, BRIDGE_CLASS = #{bridgeClass,jdbcType=VARCHAR}, FEILD_KEYS = #{feildKeys,jdbcType=VARCHAR}, IS_GLOBAL = #{isGlobal,jdbcType=VARCHAR}, IS_DURABLE = #{isDurable,jdbcType=VARCHAR}, DURATION_OF_MINUTES = #{durationOfMinutes,jdbcType=INTEGER}, LAST_LOAD_DATETIME = current_timestamp, STATUS = #{status,jdbcType=VARCHAR}, UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}, CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}, UPDATE_DATETIME = current_timestamp, CREATE_DATETIME = current_timestamp,
id = #{id, jdbcType = VARCHAR}

mapper.delete:
delete from T_CACHE_DEF
where
id = #{id, jdbcType = VARCHAR}
