mapper.select:
select

from  T_COUNTRY_3166


        and ID = #{id,jdbcType=VARCHAR}


        and ALPHA_CODE_2 = #{alphaCode2,jdbcType=VARCHAR}


        and ALPHA_CODE_3 = #{alphaCode3,jdbcType=VARCHAR}


        and NUMBER_CODE = #{numberCode,jdbcType=VARCHAR}


        and ISO3166_2 = #{iso31662,jdbcType=VARCHAR}


        and I18N_KEY = #{i18nKey,jdbcType=VARCHAR}


        and NAME = #{name,jdbcType=VARCHAR}


        and REGION = #{region,jdbcType=VARCHAR}


        and SUB_REGION = #{subRegion,jdbcType=VARCHAR}


        and INTERMEDIATE_REGION = #{intermediateRegion,jdbcType=VARCHAR}


        and REGION_CODE = #{regionCode,jdbcType=VARCHAR}


        and SUB_REGION_CODE = #{subRegionCode,jdbcType=VARCHAR}


        and INTERMEDIATE_REGION_CODE = #{intermediateRegionCode,jdbcType=VARCHAR}


        and STATUS = #{status,jdbcType=VARCHAR}


        and UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}

mapper.update:
update T_COUNTRY_3166
ID = #{id,jdbcType=VARCHAR}, ALPHA_CODE_2 = #{alphaCode2,jdbcType=VARCHAR}, ALPHA_CODE_3 = #{alphaCode3,jdbcType=VARCHAR}, NUMBER_CODE = #{numberCode,jdbcType=VARCHAR}, ISO3166_2 = #{iso31662,jdbcType=VARCHAR}, I18N_KEY = #{i18nKey,jdbcType=VARCHAR}, NAME = #{name,jdbcType=VARCHAR}, REGION = #{region,jdbcType=VARCHAR}, SUB_REGION = #{subRegion,jdbcType=VARCHAR}, INTERMEDIATE_REGION = #{intermediateRegion,jdbcType=VARCHAR}, REGION_CODE = #{regionCode,jdbcType=VARCHAR}, SUB_REGION_CODE = #{subRegionCode,jdbcType=VARCHAR}, INTERMEDIATE_REGION_CODE = #{intermediateRegionCode,jdbcType=VARCHAR}, STATUS = #{status,jdbcType=VARCHAR}, UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}, CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}, UPDATE_DATETIME = current_timestamp, CREATE_DATETIME = current_timestamp,
id = #{id, jdbcType = VARCHAR}

mapper.delete:
