mapper.select:
select

from T_DICT_DATA


        and DICT_CODE = #{dictCode,jdbcType=VARCHAR}


        and PARENT_CODE = #{parentCode,jdbcType=VARCHAR}


        and TREE_SORT = #{treeSort,jdbcType=SMALLINT}


        and TREE_LEAF = #{treeLeaf,jdbcType=VARCHAR}


        and TREE_LEVEL = #{treeLevel,jdbcType=SMALLINT}


        and DICT_LABEL = #{dictLabel,jdbcType=VARCHAR}


        and DICT_VALUE = #{dictValue,jdbcType=VARCHAR}


        and DICT_TYPE = #{dictType,jdbcType=VARCHAR}


        and DICT_TAG = #{dictTag,jdbcType=VARCHAR}


        and IS_SYS = #{isSys,jdbcType=VARCHAR}


        and IS_DEFAULT = #{isDefault,jdbcType=VARCHAR}


        and THE_DESC = #{theDesc,jdbcType=VARCHAR}


        and STATUS = #{status,jdbcType=VARCHAR}


        and UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}

mapper.update:
update T_DICT_DATA
DICT_CODE = #{dictCode,jdbcType=VARCHAR}, PARENT_CODE = #{parentCode,jdbcType=VARCHAR}, TREE_SORT = #{treeSort,jdbcType=SMALLINT}, TREE_LEAF = #{treeLeaf,jdbcType=VARCHAR}, TREE_LEVEL = #{treeLevel,jdbcType=SMALLINT}, DICT_LABEL = #{dictLabel,jdbcType=VARCHAR}, DICT_VALUE = #{dictValue,jdbcType=VARCHAR}, DICT_TYPE = #{dictType,jdbcType=VARCHAR}, DICT_TAG = #{dictTag,jdbcType=VARCHAR}, IS_SYS = #{isSys,jdbcType=VARCHAR}, IS_DEFAULT = #{isDefault,jdbcType=VARCHAR}, THE_DESC = #{theDesc,jdbcType=VARCHAR}, STATUS = #{status,jdbcType=VARCHAR}, UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}, CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}, UPDATE_DATETIME = current_timestamp, CREATE_DATETIME = current_timestamp,
dict_code = #{dictCode, jdbcType = VARCHAR}

mapper.delete:
delete from T_DICT_DATA
where
dict_code = #{dictCode, jdbcType = VARCHAR}
