mapper.select:
select

from  T_DICT_TYPE


        and ID = #{id,jdbcType=VARCHAR}


        and DICT_NAME = #{dictName,jdbcType=VARCHAR}


        and DICT_TYPE = #{dictType,jdbcType=VARCHAR}


        and THE_DESC = #{theDesc,jdbcType=VARCHAR}


        and IS_SYS = #{isSys,jdbcType=VARCHAR}


        and STATUS = #{status,jdbcType=VARCHAR}


        and UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}

mapper.update:
update T_DICT_TYPE
ID = #{id,jdbcType=VARCHAR}, DICT_NAME = #{dictName,jdbcType=VARCHAR}, DICT_TYPE = #{dictType,jdbcType=VARCHAR}, THE_DESC = #{theDesc,jdbcType=VARCHAR}, IS_SYS = #{isSys,jdbcType=VARCHAR}, STATUS = #{status,jdbcType=VARCHAR}, UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}, CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}, UPDATE_DATETIME = current_timestamp, CREATE_DATETIME = current_timestamp,
id = #{id, jdbcType = VARCHAR}

mapper.delete:
delete from T_DICT_TYPE
where
id = #{id, jdbcType = VARCHAR}
